INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31264, 12309, 'Vincristine', 'Constipation', 'Vincristine can cause constipation.  Ileus, bowel obstruction, and colonic pseudo-obstruction have occurred.  When using this agent in people at risk of constipation, appropriate measures should be instituted to prevent such complications.  A routine prophylactic regimen against constipation is recommended for all patients receiving vincristine.', '2', '"Product Information. Oncovin (vincristine)." Lilly, Eli and Company  (2001):|"Product Information. Marqibo (vinCRIStine liposome)." Talon Therapeutics Inc', 'DDInter', 0);
