INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31294, 17032, 'Vinblastine', 'Infections', 'The use of vinblastine is contraindicated in patients with known infectious diseases.  Vinblastine can induce myelosuppression.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with vinblastine.  Close clinical monitoring of hematopoietic function is recommended.  Vinblastine should not be used in patients with neutrophil counts below 1000/mm3.', '3', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31295, 29073, 'Vinblastine', 'Infections', 'The use of vinblastine is contraindicated in patients with known infectious diseases.  Vinblastine can induce myelosuppression.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with vinblastine.  Close clinical monitoring of hematopoietic function is recommended.  Vinblastine should not be used in patients with neutrophil counts below 1000/mm3.', '3', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31296, 17032, 'Vinblastine', 'Bone Marrow Failure Disorders', 'The use of vinblastine is contraindicated in patients with significant granulocytopenia.  Vinblastine induces dose-dependent leukopenia.  Thrombocytopenia and anemia have been reported infrequently during vinblastine therapy.  Therapy with vinblastine should be administered cautiously in patients whose bone marrow reserve may be severely depressed by prior chemotherapy or irradiation or whose marrow function is recovering from previous cytotoxic therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended.', '3', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31297, 29073, 'Vinblastine', 'Bone Marrow Failure Disorders', 'The use of vinblastine is contraindicated in patients with significant granulocytopenia.  Vinblastine induces dose-dependent leukopenia.  Thrombocytopenia and anemia have been reported infrequently during vinblastine therapy.  Therapy with vinblastine should be administered cautiously in patients whose bone marrow reserve may be severely depressed by prior chemotherapy or irradiation or whose marrow function is recovering from previous cytotoxic therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended.', '3', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31298, 17032, 'Vinblastine', 'Respiratory Insufficiency', 'Acute shortness of breath and bronchospasm, some severe and life-threatening, have been reported with the use of vinca alkaloids.  These reactions were observed most often during combination therapy with mitomycin C, occurring within minutes to several hours after administration of the vinca alkaloid or up to 2 weeks following the mitomycin dose.  Therapy with vinca alkaloids should be administered cautiously in patients with preexisting pulmonary dysfunction.  Aggressive treatment may be necessary, including use of supplemental oxygen, bronchodilators, and/or corticosteroids.  In patients who develop progressive dyspnea requiring chronic therapy, vinca alkaloid should not be readministered.', '3', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):|"Product Information. Oncovin (vincristine)." Lilly, Eli and Company  (2001):|"Product Information. Navelbine (vinorelbine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31299, 29073, 'Vinblastine', 'Respiratory Insufficiency', 'Acute shortness of breath and bronchospasm, some severe and life-threatening, have been reported with the use of vinca alkaloids.  These reactions were observed most often during combination therapy with mitomycin C, occurring within minutes to several hours after administration of the vinca alkaloid or up to 2 weeks following the mitomycin dose.  Therapy with vinca alkaloids should be administered cautiously in patients with preexisting pulmonary dysfunction.  Aggressive treatment may be necessary, including use of supplemental oxygen, bronchodilators, and/or corticosteroids.  In patients who develop progressive dyspnea requiring chronic therapy, vinca alkaloid should not be readministered.', '3', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):|"Product Information. Oncovin (vincristine)." Lilly, Eli and Company  (2001):|"Product Information. Navelbine (vinorelbine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31300, 17032, 'Vinblastine', 'Liver Diseases', 'Vinblastine is metabolized by the liver and eliminated primarily via biliary excretion.  Patients with hepatic impairment may be at increased risk for toxicity associated with vinblastine therapy.  Therapy with vinblastine should be administered cautiously in patients with compromised hepatic function.', '2', '"Product Information. Velban (vinblastine)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
