INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31380, 23315, 'Vinorelbine', 'Gastrointestinal Diseases', 'Severe and fatal paralytic ileus, constipation, intestinal obstruction, necrosis, and perforation occur in patients receiving vinorelbine injection.  Institute a prophylactic bowel regimen to mitigate potential constipation, bowel obstruction and/or paralytic ileus, considering adequate dietary fiber intake, hydration and routine use of stool softeners.  Caution and monitoring is advised in patients with history of gastrointestinal issues.', '2', '"Product Information. Navelbine (vinorelbine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
