INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31593, 10558, 'Zaleplon', 'Liver Diseases', 'Zaleplon is primarily metabolized by the liver.  The oral clearance of zaleplon has been shown to decrease by 70% and 87% in patients with compensated and decompensated cirrhosis, respectively, compared to healthy patients.  Associated plasma drug concentrations were markedly increased by several-fold.  Therapy with zaleplon is not recommended in patients with severely impaired hepatic function.  The dosage should be reduced to 5 mg in patients with mild to moderate hepatic impairment.', '3', '"Product Information. Sonata (zaleplon)." Wyeth-Ayerst Laboratories  (2001):|Dooley M, Plosker GL "Zaleplon - A review of its use in the treatment of insomnia." Drugs 60 (2000):  413-45', 'DDInter', 0);
