INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33723, 2718, 'Bosutinib', 'Cardiovascular Diseases', 'Cardiovascular events, including arterial vascular occlusive events, peripheral arterial occlusive events and ischemic cerebrovascular events have been reported in patients receiving tyrosine kinase inhibitors.  If acute signs or symptoms of cardiovascular events occur, patients should seek immediate medical attention.  The cardiovascular status and risk factors of patients should be evaluated prior to therapy and cardiovascular monitoring and management should take place during treatment.', '2', '"Product Information. Gleevec (imatinib)." Novartis Pharmaceuticals  (2022):|"Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb  (2006):|"Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals  (2007):', 'DDInter', 0);
