INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33726, 2718, 'Bosutinib', 'Water-Electrolyte Imbalance', 'Fluid retention occurs with BCR-ABL tyrosine kinase inhibitors therapy and may manifest as pericardial effusion, pleural effusion, pulmonary edema, and/or peripheral edema.  Caution should be taken when using these drugs in patients with preexisting fluid retention or congestive heart failure.  Monitor and manage patients using standards of care.  Interrupt, reduce dose or discontinue therapy as necessary.', '2', '"Product Information. Gleevec (imatinib)." Novartis Pharmaceuticals  (2022):|"Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb  (2006):|"Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals  (2007):|"Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
