INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33729, 2718, 'Bosutinib', 'Bone Marrow Failure Disorders', 'Thrombocytopenia, aplastic anemia, agranulocytosis and neutropenia occur with BCR-ABL tyrosine kinase inhibitors.  Therapy with these drugs should be administered cautiously in patients with preexisting bone marrow suppression.  A complete blood count should be performed every 1-2 weeks for the first month of therapy and then monthly thereafter, or as clinically indicated.  To manage myelosuppression, withhold, reduce dose, or discontinue therapy as necessary.', '2', '"Product Information. Gleevec (imatinib)." Novartis Pharmaceuticals  (2022):|"Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb  (2006):|"Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals  (2007):|"Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
