INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33748, 15372, 'Bleomycin', 'Kidney Diseases', 'Bleomycin is primarily eliminated by the kidney.  Approximately 60% to 70% of bleomycin is excreted in the urine as active drug.  Moderate renal failure reduces elimination to < 20% of the drug.  Extreme caution should be exercised when administering bleomycin in patients with existing or predisposition to significantly compromised renal function.  Close clinical monitoring of renal function is recommended.', '3', 'McLeod BF, Lawrence HJ, Smith DW, Vogt PJ, Gandara DR "Fatal bleomycin toxicity from a low cumulative dose in a patient with renal insufficiency." Cancer 60 (1987):  2617-20|Bennett WM, Pastore L, Houghton DC "Fatal pulmonary bleomycin toxicity in cisplatin-induced acute renal failure." Cancer Treat Rep 64 (1980):  921-4|"Product Information. Blenoxane (bleomycin)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
