INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33750, 15372, 'Bleomycin', 'Hepatic Insufficiency', 'The effect of hepatic insufficiency on the pharmacokinetics of bleomycin has not been evaluated.  However, hepatic toxicity, beginning as a deterioration in liver function tests, has been reported.  These toxicities may occur at any time after initiation of therapy.  Caution and monitoring is advised if bleomycin is used in patients with liver impairment.', '2', '"Product Information. Blenoxane (bleomycin)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
