INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33952, 313, 'Brimonidine (ophthalmic)', 'Liver Failure', 'Topically applied alpha-2 adrenergic receptor agonists are systemically absorbed, with the potential for producing rare but clinically significant systemic effects.  There are limited data concerning the pharmacokinetic disposition or the clinical use of these drugs in patients with renal and/or liver disease.  Therapy with ophthalmic alpha-2 adrenergic agents should be administered cautiously in patients with significantly impaired renal or hepatic function.', '2', '"Product Information. Iopidine (apraclonidine ophthalmic)." Alcon Laboratories Inc|"Product Information. Alphagan (brimonidine ophthalmic)." Allergan Inc  (2001):', 'DDInter', 0);
