INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (36698, 914, 'Carbamazepine', 'Thyroid Diseases', 'Decreased values for thyroid function tests have been observed with carbamazepine administration.  One study reported decreased serum levels of T4, FT4, and T3.  In that study, TSH concentrations were not significantly altered.  Clinicians should be cognizant of these effects when prescribing or administering carbamazepine therapy to patients with thyroid disorders.', '2', 'Strandjord RE, AAnderud S, Myking OL, Johannessen SI "Influence of carbamazepine on serum thyroxine and triiodothyronine in patients with epilepsy." Acta Neurol Scand 63 (1981):  111-21|"Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals  (2002):|Isojarvi JI, Airaksinen KE, Repo M, Pakarinen AJ, Salmela P, Myllyla VV "Carbamazepine, serum thyroid hormones and myocardial function in epileptic patients." J Neurol Neurosurg Psychiatry 56 (1993):  710-2|Delzompo M, Bocchetta A, Loviselli A, Martino E, Post RM, Ketter TA "Thyroid function during carbamazepine." Biol Psychiatry 36 (1994):  135-6', 'DDInter', 0);
