INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47303, 3789, 'Diflunisal', 'Water-Electrolyte Imbalance', 'Fluid retention and edema have been reported in association with the use of nonsteroidal anti-inflammatory drugs (NSAIDs).  Therapy with NSAIDs should be administered cautiously in patients with preexisting fluid retention, hypertension, or a history of heart failure.  Blood pressure and cardiovascular status should be monitored closely during the initiation of NSAID treatment and throughout the course of therapy.', '3', '"Product Information. Motrin (ibuprofen)." Pharmacia and Upjohn  (2002):|"Product Information. Nalfon (fenoprofen)." Xspire Pharma  (2002):|"Product Information. Indocin (indomethacin)." Merck & Co., Inc  (2002):|"Product Information. Orudis (ketoprofen)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc  (2002):|"Product Information. Anaprox (naproxen)." Roche Laboratories  (2006):|"Product Information. Clinoril (sulindac)." Merck & Co., Inc  (2001):|"Product Information. Tolectin (tolmetin)." McNeil Pharmaceutical  (2001):|"Product Information. Relafen (nabumetone)." SmithKline Beecham  (2001):|"Product Information. Feldene (piroxicam)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Ansaid (flurbiprofen)." Pharmacia and Upjohn  (2001):|"Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daypro (oxaprozin)." Searle  (2001):|"Product Information. Mobic (meloxicam)." Boehringer-Ingelheim  (2001):', 'DDInter', 0);
