INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47355, 13718, 'Alprostadil', 'Hemorrhage', 'Alprostadil inhibits platelet aggregation.  Caution is recommended when using alprostadil injection in neonates with bleeding tendencies.', '2', '"Product Information. Caverject (alprostadil)." Pharmacia and Upjohn|"Product Information. Prostin VR Pediatric (alprostadil)." Pharmacia and Upjohn', 'DDInter', 0);
