INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47480, 5824, 'Dimethyl fumarate', 'Lymphopenia', 'The use of dimethyl fumarate may decrease lymphocyte counts.  Dimethyl fumarate has not been studied in patients with preexisting low lymphocyte counts; caution is advised in these patients.  CBC (including lymphocyte count) should be obtained before initiating treatment with dimethyl fumarate, 6 months after starting treatment, every 6 to 12 months thereafter, and as clinically indicated.  Interruption of therapy in patients with lymphocyte counts less than 0.5 x 10(9) cells/L persisting for more than 6 months and withholding treatment from patients with serious infections until resolution should be considered.  Caution is recommended and decisions about whether or not to restart dimethyl fumarate should be individualized based on clinical circumstances.', '2', '"Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Inc  (2023):', 'DDInter', 0);
