INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47684, 14102, 'Dipyridamole', 'Long QT Syndrome', 'Serious adverse reactions associated with the administration of intravenous dipyridamole have included cardiac death, fatal and non- fatal myocardial infarction, ventricular fibrillation, symptomatic ventricular tachycardia, stroke, transient cerebral ischemia, seizures, anaphylactoid reaction and bronchospasm.  There have been reported cases of asystole, sinus node arrest, sinus node depression and conduction block.  Patients with abnormalities of cardiac impulse formation/conduction or severe coronary artery disease may be at increased risk for these events.  Caution is recommended.', '2', '"Product Information. Persantine (dipyridamole)." Boehringer-Ingelheim  (2002):', 'DDInter', 0);
