INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51631, 13308, 'Filgrastim', 'Leukemia, Myeloid', 'Colony stimulating factors primarily stimulates the proliferation of neutrophils but may also, theoretically, enhance tumor growth, particularly in myeloid malignancies.  Therapy with these drugs should be administered cautiously in patients with myeloid tumors.  Additionally, if used for PBPC (peripheral blood progenitor cell) collection in such patients, tumor cells may be released from the marrow and collected in the leukapheresis product.  The effect of reinfusion of tumor cells is uncertain at this time.', '3', '"Product Information. Neupogen (filgrastim)." Amgen  (2002):|"Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):', 'DDInter', 0);
