INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52006, 14155, 'Flumazenil', 'Seizures', 'Flumazenil has been associated with seizure activity, particularly in patients receiving repeated doses of benzodiazepines for long term sedation, acute overdose involving concurrent cyclic antidepressants, or head injury where it may alter cerebral blood flow in patients receiving benzodiazepines.  Therapy with flumazenil should be administered cautiously in patients with a history of or predisposition to seizures.', '3', 'Marchant B, Wray R, Leach A, Nama M "Flumazenil causing convulsions and ventricular tachycardia." BMJ 299 (1989):  860|Mordel A, Winkler E, Almog S, Tirosh M, Ezra D "Seizures after flumazenil administration in a case of combined benzodiazepine and tricyclic antidepressant overdose." Crit Care Med 20 (1992):  1733-4|Thomas P, Lebrun C, Chatel M "De novo absence status epilepticus as a benzodiazepine withdrawal syndrome." Epilepsia 34 (1993):  355-8|"Product Information. Romazicon (flumazenil)." Roche Laboratories  (2001):|Chern TL, Kwan A "Flumazenil-induced seizure accompanying benzodiazepine and baclofen intoxication." Am J Emerg Med 14 (1996):  231-2', 'DDInter', 0);
