INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52012, 14155, 'Flumazenil', 'Psychotic Disorders', 'Panic attacks have been induced by flumazenil in patients with a history of panic attacks.  Emotional lability, including crying, depersonalization, euphoria, depression, dysphoria and paranoia have also been reported.  Therapy with flumazenil should be administered cautiously in patients with or predisposed to psychosis, particularly panic attacks.', '2', 'Seebach J, Jost R "Flumazenil-induced psychotic disorder in hepatic encephalopathy." Lancet 339 (1992):  488-9|"Flumazenil." Med Lett Drugs Ther 34 (1992):  66-8|Griffiths RR, Evans SM, Guarino JJ, Roache JD, Furman WR, Liebson I, Schwam EM "Intravenous flumazenil following acute and repeated exposure to lorazepam in healthy volunteers: antagonism and precipitated withdrawal." J Pharmacol Exp Ther 265 (1993):  1163-74|"Product Information. Romazicon (flumazenil)." Roche Laboratories  (2001):', 'DDInter', 0);
