INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52026, 3076, 'Fludrocortisone', 'Peptic Ulcer Perforation', 'Corticosteroids may cause gastrointestinal perforation and hemorrhage, usually when given in high dosages or for prolonged periods.  They may also mask symptoms of complications such as peritonitis or intraabdominal sepsis.  Therapy with corticosteroids should be avoided or administered cautiously in patients with diverticulitis, nonspecific ulcerative colitis (if there is a probability of impending perforation, abscess, or other pyogenic infection), or recent intestinal anastomoses.', '2', '"Product Information. Hydeltrasol (prednisolone)." Merck & Co., Inc  (2001):|"Product Information. Deltasone (prednisone)." Pharmacia and Upjohn  (2001):|"Product Information. Decadron (dexamethasone)." Merck & Co., Inc  (2001):|"Product Information. Hydrocortone (hydrocortisone)." Merck & Co., Inc  (2001):|"Product Information. Medrol (methylprednisolone)." Pharmacia and Upjohn  (2001):|"Product Information. Florinef Acetate (fludrocortisone)." Bristol-Myers Squibb  (2001):|"Product Information. Cortone Acetate (cortisone)." Merck & Co., Inc  (2001):|"Product Information. Kenalog (triamcinolone)." Bristol-Myers Squibb  (2001):|"Product Information. Celestone (betamethasone)." Schering Corporation  (2001):|"Product Information. Emflaza (deflazacort)." PTC Therapeutics, Inc.  (2021):', 'DDInter', 0);
