INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52107, 3, 'Fluorouracil', 'Coronary Artery Disease', 'Fluorouracil can cause cardiotoxicity, including angina, myocardial infarction, arrhythmia and heart failure based on post marketing reports.  Reported risk factors for cardiotoxicity include administration by continuous infusion rather than intravenous bolus and presence of coronary artery disease.  Caution and monitoring is advised in patients with coronary artery disease.  Treatment should be discontinued for cardiotoxicity.  The risks of treatment resumption after cardiotoxicity has been resolved have not been established.', '2', '"Product Information. Fluorouracil (fluorouracil)." BluePoint Laboratories  (2017):', 'DDInter', 0);
