INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52524, 8269, 'Fluphenazine', 'Alcoholic Intoxication', 'The use of phenothiazines is contraindicated in patients with acute alcohol intoxication exhibiting depressed vital signs.  The central nervous system depressant effects of phenothiazines may be additive with those of alcohol.  Severe respiratory depression and respiratory arrest may occur.  Therapy with phenothiazines should be administered cautiously in patients who might be prone to acute alcohol intake.', '3', '"Product Information. Thorazine (chlorpromazine)." SmithKline Beecham  (2002):|"Product Information. Sparine (promazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Prolixin (fluphenazine)." Bristol-Myers Squibb  (2001):|"Product Information. Compazine (prochlorperazine)." SmithKline Beecham  (2001):|"Product Information. Mellaril (thioridazine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Serentil (mesoridazine)." Boehringer-Ingelheim  (2001):|"Product Information. Torecan (thiethylperazine)." Roxane Laboratories Inc  (2001):|"Product Information. Trilafon (perphenazine)." Schering Corporation  (2001):|"Product Information. Tacaryl (methdilazine)." Westwood Squibb Pharmaceutical Corporation  (2001):|"Product Information. Temaril (trimeprazine)." Allergan Inc  (2001):|"Product Information. Stelazine (trifluoperazine)." SmithKline Beecham  (2001):|"Product Information. Vesprin (triflupromazine)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
