INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52530, 8269, 'Fluphenazine', 'Craniocerebral Trauma', 'The use of phenothiazines is contraindicated in patients with suspected or established subcortical brain damage, with or without hypothalamic involvement.  Phenothiazines can interfere with thermoregulatory mechanisms, and a hyperthermic reaction with temperatures in excess of 104 F may occur in such patients, sometimes not until 14 to 16 hours after drug administration.', '3', '"Product Information. Thorazine (chlorpromazine)." SmithKline Beecham  (2002):|"Product Information. Sparine (promazine)." Wyeth-Ayerst Laboratories  (2001):|Dilsaver SC "Effects of neuroleptics on body temperature" J Clin Psychiatry 49 (1988):  78-9|Caroff S, Rosenberg H, Gerber JC "Neuroleptic malignant syndrome and malignant hyperthermia" Lancet 1 (1983):  244|Keshavan MS, Kambhampati RK "Prolonged fever without extrapyramidal symptoms during neuroleptic  treatment" J Clin Psychopharmacol 9 (1989):  230-1|"Product Information. Prolixin (fluphenazine)." Bristol-Myers Squibb  (2001):|"Product Information. Compazine (prochlorperazine)." SmithKline Beecham  (2001):|"Product Information. Mellaril (thioridazine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Serentil (mesoridazine)." Boehringer-Ingelheim  (2001):|"Product Information. Torecan (thiethylperazine)." Roxane Laboratories Inc  (2001):|"Product Information. Trilafon (perphenazine)." Schering Corporation  (2001):|"Product Information. Stelazine (trifluoperazine)." SmithKline Beecham  (2001):|"Product Information. Vesprin (triflupromazine)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
