INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52640, 5060, 'Folic acid', 'Anemia', 'The use of folic acid is contraindicated in patients with undiagnosed anemia.  Folic acid in dosages above 1 mg/day can obscure the diagnosis of pernicious anemia by alleviating the hematologic abnormalities while allowing the progression of neurologic complications.  In addition, folic acid alone is improper therapy in the treatment of pernicious anemia and other megaloblastic anemias where vitamin B12 is deficient.', '3', 'Butterworth CE Jr, Tamura T "Folic acid safety and toxicity: a brief review." Am J Clin Nutr 50 (1989):  353-8|Katz M "Potential danger of self-medication with folic acid." N Engl J Med 289 (1973):  1095|"Product Information. Renal Multivitamin Formula Rx (folic acid)." Vitaline Corporation, Ashland, OR.', 'DDInter', 0);
