INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52900, 11749, 'Amitriptyline', 'Diabetes Mellitus', 'Both elevation and lowering of blood sugar levels have been reported with the use of some tricyclic antidepressants (TCAs).  Rarely, these effects have also occurred with maprotiline, a tetracyclic antidepressant.  Patients with diabetes should be monitored for worsening control of blood glucose when treated with these agents, particularly during dosage escalation or whenever dosage has been altered.', '2', '"Product Information. Pamelor (nortriptyline)." Sandoz Pharmaceuticals Corporation  (2002):|"Product Information. Elavil (amitriptyline)." Stuart Pharmaceuticals  (2002):|"Product Information. Norpramin (desipramine)." Hoechst Marion Roussel  (2002):|"Product Information. Sinequan (doxepin)." Roerig Division  (2002):|"Product Information. Tofranil (imipramine)." Novartis Pharmaceuticals  (2002):|Zogno MG, Tolfo L, Draghi E "Hypoglycemia caused by maprotiline in a patient taking oral antidiabetics." Ann Pharmacother 28 (1994):  406|"Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals  (2001):|"Product Information. Asendin (amoxapine)." Lederle Laboratories  (2001):|"Product Information. Ludiomil (maprotiline)." Ciba-Geigy Pharmaceuticals  (2001):|"Product Information. Surmontil (trimipramine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Vivactil (protriptyline)." Merck & Co., Inc  (2001):', 'DDInter', 0);
