INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52948, 11749, 'Amitriptyline', 'Glaucoma', 'Tricyclic antidepressants as other type of antidepressants have an effect on pupil size causing dilation.  This effect can potentially narrow the eye angle resulting in increased intraocular pressure and angle closure glaucoma, especially in predisposed patients.  These drugs should be used with caution in patients with anatomically narrow angle or history of glaucoma.  Doxepin hydrochloride capsules are contraindicated in patients with glaucoma.', '2', '"Product Information. Pamelor (nortriptyline)." Sandoz Pharmaceuticals Corporation  (2002):|"Product Information. Elavil (amitriptyline)." Stuart Pharmaceuticals  (2002):|"Product Information. Norpramin (desipramine)." Hoechst Marion Roussel  (2002):|"Product Information. Tofranil (imipramine)." Novartis Pharmaceuticals  (2002):|"Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals  (2001):|"Product Information. Asendin (amoxapine)." Lederle Laboratories  (2001):|"Product Information. Surmontil (trimipramine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Vivactil (protriptyline)." Merck & Co., Inc  (2001):', 'DDInter', 0);
