INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52996, 8300, 'Fosinopril', 'Liver Failure', 'Fosinopril is primarily metabolized by hepatic and gut wall esterases to its active moiety, fosinoprilat, patients with impaired liver function could develop elevated plasma levels of unchanged fosinopril.  In a study in patients with alcoholic or biliary cirrhosis, the extent of hydrolysis was unaffected, although the rate was slowed.  In these patients, the apparent total body clearance of fosinoprilat was decreased and the plasma AUC approximately doubled.', '2', '"Product Information. Monopril (fosinopril)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
