INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58055, 1962, 'Irinotecan', 'Diarrhea', 'Irinotecan liposomal can cause severe and life-threatening diarrhea.  Do not administer Irinotecan liposomal to patients with bowel obstruction.  It is recommended to withhold treatment with irinotecan liposomal for Grade 2-4 diarrhea and to initiate appropriate clinical measures for diarrhea of any severity.  The dose of irinotecan liposomal should be reduced according to manufacturer indications following recovery to Grade 1 diarrhea.  Care should be exercised when using this agent in patients at risk of diarrhea episodes.', '3', '"Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals  (2015):', 'DDInter', 0);
