INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58073, 1962, 'Irinotecan', 'Liver Failure', 'The metabolism of irinotecan liposomal has not been evaluated.  Irinotecan is primary metabolized by the liver to a biologically active metabolite.  The pharmacokinetic disposition of irinotecan liposomal in patients with hepatic impairment has not been formally assessed.  Therapy with irinotecan liposomal should be administered cautiously to patients with or predisposed to compromised hepatic function.  Clinical monitoring of hepatic function is recommended.', '2', '"Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals  (2015):', 'DDInter', 0);
