INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58208, 884, 'Irbesartan', 'Kidney Diseases', 'Changes in renal function including acute renal failure can be caused by drugs that inhibit the renin-angiotensin system and by diuretics.  Patients whose renal function may depend in part on the activity of the renin-angiotensin system (e.g., patients with renal artery stenosis, chronic kidney disease, severe congestive heart failure, or volume depletion) may be at particular risk of developing acute renal failure with these agents.  Monitor renal function periodically in these patients.  Consider withholding or discontinuing therapy in patients who develop a clinically significant decrease in renal function with these agents.', '2', '"Product Information. Cozaar (losartan)." Merck & Co., Inc  (2001):|"Product Information. Diovan (valsartan)." Novartis Pharmaceuticals  (2001):|"Product Information. Avapro (irbesartan)." Bristol-Myers Squibb  (2001):|"Product Information. Teveten (eprosartan)." SmithKline Beecham  (2001):|"Product Information. Atacand (candesartan)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Micardis (telmisartan)." Boehringer-Ingelheim  (2001):|"Product Information. Benicar (olmesartan)." Sankyo Pharma  (2002):|"Product Information. Edarbi (azilsartan)." Takeda Pharmaceuticals America  (2011):', 'DDInter', 0);
