INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58303, 0, 'Isoprenaline', 'Tachycardia', 'The use of isoproterenol is contraindicated in patients with tachyarrhythmias; tachycardia or heart block caused by digitalis intoxication; ventricular arrhythmias which require inotropic therapy; and angina pectoris.', '3', '"Product Information. Isuprel (isoproterenol)." Sanofi Winthrop Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58304, 0, 'Isoprenaline', 'Dehydration', 'The use of sympathomimetic amines has been infrequently associated with significant hypotension especially in dehydrated patients secondary to the drug''s beta-2 mediated vasodilation.  Hypovolemia should be corrected, if possible, before administering sympathomimetic amines.  Blood pressure and ECG should be monitored at regular intervals.  Monitoring of cardiac output and pulmonary wedge pressure may also be desired.', '3', '"Product Information. Isuprel (isoproterenol)." Sanofi Winthrop Pharmaceuticals  (2001):|"Product Information. Epifrin (EPINEPHrine ophthalmic)." Allergan Inc  (2022):|"Product Information. Adrenalin (EPINEPHrine)." Apothecon Inc  (2022):|"Product Information. Levophed Bitartrate (norepinephrine)." Sanofi Winthrop Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58305, 0, 'Isoprenaline', 'Acidosis', 'Acidosis, hypoxia, and hypercapnia may reduce the effectiveness of sympathomimetic amines in raising blood pressure.  These conditions should be corrected before initiating therapy with sympathomimetic amines, if possible.  Monitoring the patients acid-base balance, carbon dioxide levels, and oxygen saturation is recommended.', '2', '"Product Information. Isuprel (isoproterenol)." Sanofi Winthrop Pharmaceuticals  (2001):|"Product Information. Epifrin (EPINEPHrine ophthalmic)." Allergan Inc  (2022):|"Product Information. Adrenalin (EPINEPHrine)." Apothecon Inc  (2022):|"Product Information. Levophed Bitartrate (norepinephrine)." Sanofi Winthrop Pharmaceuticals  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58306, 4128, 'Isotretinoin', 'Intracranial Hypertension', 'The use of retinoids has been associated with cases of pseudotumor cerebri (benign intracranial hypertension).  Early signs and symptoms include papilledema, headache, nausea and vomiting, and visual disturbances.  Patients who experience symptoms of this disorder while on retinoid therapy should be referred to a neurologist.  If the diagnosis is confirmed, the retinoid should be discontinued permanently.', '3', '"Product Information. Accutane (isotretinoin)." Roche Laboratories  (2001):|"Product Information. Soriatane (acitretin)." Roche Laboratories  (2001):', 'DDInter', 0);
