INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58431, 4017, 'Isosorbide mononitrate', 'Glaucoma', 'Some medical references state that organic nitrates and nitrites may increase intraocular pressure and should be used with caution in patients with glaucoma.  However, the effect on intraocular pressure is variable and brief, and there is no evidence that these drugs precipitate narrow-angle glaucoma.  Amyl nitrate typically may cause a slight rise of 3+ mm for several seconds followed by a fall in intraocular pressure for 10 to 20 minutes, the latter secondary to a fall in blood pressure.  Nitroglycerin rarely produces ocular side effects, and oral nitroglycerin appears to have few to no significant ocular side effects.', '1', 'Fraunfelder FT, Fraunfelder FW; Randall JA "Drug-Induced Ocular Side Effects" Boston, MA: Butterworth-Heinemann  (2001):', 'DDInter', 0);
