INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58440, 0, 'Isradipine', 'Intestinal Obstruction', 'The controlled-release formulation of isradipine (Dynacirc CR) contains a non-deformable material.  There have been rare reports of obstructive symptoms in patients with known strictures following the ingestion of similar sustained-release products.  Therapy with the controlled-release formulation of isradipine should be administered cautiously in patients with preexisting severe gastrointestinal narrowing or obstruction, whether pathologic or iatrogenic.', '2', '"Product Information. Dynacirc (isradipine)." Sandoz Pharmaceuticals Corporation', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58441, 1691, 'Ivabradine', 'Cardiac Conduction System Disease', 'The use of ivabradine is contraindicated in sick sinus syndrome, sinoatrial block, or 3rd degree AV block, unless a functioning demand pacemaker is present, in those patients that are pacemaker dependent (heart rate maintained exclusively by the pacemaker), and in patients with resting heart rate less than 60 bpm prior to treatment.', '3', '"Product Information. Corlanor (ivabradine)." Amgen USA  (2015):', 'DDInter', 0);
