INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58532, 959, 'Itraconazole', 'Liver Diseases', 'Limited data are available on the use of oral itraconazole in patients with liver dysfunction.  Itraconazole has been associated with rare cases of serious hepatotoxicity (including liver failure and death).  Caution is recommended when this drug is used in patients with liver dysfunction; such patients should be carefully monitored during therapy.  Treatment with itraconazole is strongly discouraged in patients with elevated or abnormal liver enzymes, with active liver disease, or who had liver toxicity with other drugs unless the situation is serious or life-threatening and the expected benefit exceeds the risk.  Liver function monitoring is recommended for patients with preexisting hepatic function abnormalities or those who had liver toxicity with other drugs.', '3', '"Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2002):|"Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2022):|"Product Information. Tolsura (itraconazole)." Mayne Pharma Inc  (2021):', 'DDInter', 0);
