INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58568, 959, 'Itraconazole', 'Achlorhydria', 'Itraconazole 100-mg capsules should be administered after a full meal.  Under fasted conditions, absorption of itraconazole from the 100-mg capsule formulation was diminished with decreased gastric acidity.  Studies conducted under fasted conditions showed that taking the 100-mg capsule formulation with 8 ounces of a non-diet cola beverage enhanced absorption of itraconazole in AIDS patients with relative or absolute achlorhydria; this increase relative to the effects of a full meal is unknown.', '2', '"Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2002):', 'DDInter', 0);
