INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (58604, 959, 'Itraconazole', 'Immune Deficiency Disease', 'The oral bioavailability of itraconazole 100-mg capsules may be decreased in some immunocompromised patients (e.g., neutropenic patients, AIDS patients, organ transplant recipients); in addition, since hypochlorhydria has been reported in HIV-infected patients, the absorption of itraconazole may be reduced in these patients.  The dose of itraconazole 100-mg capsules should be adjusted based on the clinical response of such patients.Itraconazole oral solution is not recommended for initial treatment in severely neutropenic patients at immediate risk of systemic candidiasis.', '2', '"Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2002):|"Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2022):', 'DDInter', 0);
