INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1081, 15465, 'Modafinil', 'Administration with food may delay the absorption of modafinil (the racemate) and armodafinil (the R-enantiomer) without significantly affecting their overall bioavailability.  According to the product labeling, modafinil''s absorption may be delayed by approximately one hour if taken with food.  Similarly, the time to reach peak plasma concentration (Tmax) of armodafinil may be delayed by approximately 2 to 4 hours in the fed state.', 'dairy products', 'Minor', 'Modafinil should be administered at least 2 hours before (4 hours for dairy products) or 4 hours after meals.', NULL, NULL, 'DDInter', 0);
