INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1272, 0, 'Nifurtimox', 'Use of alcohol or products containing alcohol during therapy with nitrofurans may result in a disulfiram-like reaction in some patients.  The presumed mechanism is inhibition of aldehyde dehydrogenase (ALDH) by nitrofurans in a manner similar to disulfiram.', 'alcohol', 'Major', 'Consumption of alcohol is considered contraindicated during nifurtimox therapy.  To ensure maximal oral absorption, nifurtimox should be administered with food.', NULL, NULL, 'DDInter', 0);
