INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1301, 10188, 'Octreotide', 'Due to their gastrointestinal pharmacologic effects, somatostatin analogs (e.g., octreotide, lanreotide) may variously affect the absorption of dietary nutrients and concomitantly administered oral medications.  Somatostatin analogs have been shown to prolong gastrointestinal transit time and inhibit intestinal absorption of some nutrients such as fat.', 'food', 'Moderate', 'Clinicians should be aware of the potential for altered absorption of concomitantly administered oral medications during treatment with somatostatin analogs.  Blood levels and clinical response should be monitored, particularly for drugs that have a narrow therapeutic index, and the dosages adjusted as necessary.', NULL, NULL, 'DDInter', 0);
