INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1354, 0, 'Atovaquone', 'Food, particularly high-fat food, significantly enhances the oral absorption and bioavailability of atovaquone.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, atovaquone products (suspension, tablet, or in combination with proguanil) should be administered with a meal or milky drink, or enteral nutrition at the same time(s) each day.  Because plasma atovaquone concentrations have been shown to correlate with the likelihood of successful treatment and in some cases, survival, alternative therapies may be appropriate for patients who have difficulty taking atovaquone with food.', NULL, NULL, 'DDInter', 0);
