INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1415, 1088, 'Atorvastatin', 'Coadministration with grapefruit juice may increase the plasma concentrations of atorvastatin.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients receiving therapy with atorvastatin should limit their consumption of grapefruit juice to no more than 1 liter per day.  Patients should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.  In addition, patients should either refrain from the use of oat bran and pectin or, if concurrent use cannot be avoided, to separate the administration times by at least 2 to 4 hours.', NULL, NULL, 'DDInter', 0);
