INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1474, 0, 'Oliceridine', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including oliceridine.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Major', 'Patients should not consume alcoholic beverages or use drug products that contain alcohol during treatment with oliceridine.  Any history of alcohol or illicit drug use should be considered when prescribing oliceridine, and therapy initiated at a lower dosage if necessary.  Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension.  Due to a high degree of interpatient variability with respect to grapefruit juice interactions, patients treated with oliceridine should preferably avoid the consumption of grapefruit and grapefruit juice.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1475, 52, 'Olanzapine', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
