INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1510, 0, 'Omadacycline', 'Administration with food, particularly dairy products, may reduce the absorption of omadacycline.  The calcium content of these foods may form nonabsorbable chelates with omadacycline.', 'dairy products', 'Moderate', 'Omadacycline should be administered at least 2 hours before (4 hours for dairy products) or 4 hours after meals.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1511, 22141, 'Olaparib', 'Grapefruit juice may significantly increase the plasma concentrations of olaparib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'Patients treated with olaparib should avoid consumption of grapefruit, grapefruit juice, starfruit (carambola), and Seville oranges.', NULL, NULL, 'DDInter', 0);
