INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1712, 0, 'Panobinostat', 'Grapefruit juice may increase the plasma concentrations of panobinostat.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients should avoid consumption of grapefruit or grapefruit juice during treatment with panobinostat.  The manufacturer also recommends avoiding star fruit, Seville oranges, pomegranate, and pomegranate juice.  Panobinostat may be administered with or without food.', NULL, NULL, 'DDInter', 0);
