INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1748, 16931, 'Penicillamine', 'Food may interfere with the gastrointestinal absorption of penicillamine.', 'food', 'Moderate', 'Penicillamine should be administered on an empty stomach, at least one hour before or two hours after meals, and at least one hour apart from any other drug, food, or milk.  This permits maximum absorption and reduces the likelihood of inactivation by metal binding in the gastrointestinal tract.', NULL, NULL, 'DDInter', 0);
