INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1783, 0, 'Phentermine', 'Alcohol may potentiate the central nervous system and cardiovascular effects of centrally-acting appetite suppressants.', 'alcohol', 'Moderate', 'Concomitant use of centrally-acting appetite suppressants and alcohol should be avoided if possible, especially in patients with a history of cardiovascular disease.  Patients should be counselled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
