INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3025, 4704, 'Tinidazole', 'Use of alcohol or products containing alcohol during nitroimidazole therapy may result in a disulfiram-like reaction in some patients.', 'alcohol', 'Moderate', 'Because clear evidence is lacking concerning the safety of ethanol use during nitroimidazole therapy, patients should be apprised of the potential for interaction and instructed to avoid alcoholic beverages and products containing alcohol or propylene glycol while using oral, intravenous, or vaginal preparations of a nitroimidazole.  Alcoholic beverages should not be consumed for up to 3 days after completion of systemic nitroimidazole therapy.', NULL, NULL, 'DDInter', 0);
