INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3040, 2094, 'Tizanidine', 'The sedative effect of tizanidine may be potentiated by concomitant use of other agents with central nervous system (CNS) depressant effects.  In addition, tizanidine and many of these agents (e.g., alcohol, anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, muscle relaxants) also can exhibit hypotensive effects, which may be additive during coadministration and may increase the risk of symptomatic hypotension and orthostasis, particularly during initiation of therapy or dose escalation.', 'alcohol', 'Moderate', 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to avoid rising abruptly from a sitting or recumbent position.  Patients should contact their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or excessive CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
