INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3275, 2471, 'Varenicline', 'Varenicline may enhance the effects of alcohol as well as alter the way an individual reacts to alcohol.  During postmarketing use, some patients have reported experiencing increased intoxicating effects of alcohol while taking varenicline.', 'alcohol', 'Moderate', 'Patients should be advised to limit their consumption of alcohol until they know whether varenicline affects their tolerance for alcohol, and to exercise caution driving or operating machinery until they know how quitting smoking and/or varenicline may affect them.  Patients should immediately stop taking varenicline and contact their physician if they develop agitation, hostility, aggressive behavior, depressed mood, or changes in behavior or thinking that are not typical for them, or if they develop suicidal ideation or behavior.', NULL, NULL, 'DDInter', 0);
