INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3378, 28911, 'Valganciclovir', 'Food increases the bioavailability of ganciclovir from the prodrug, valganciclovir.  In 16 HIV-positive subjects, the administration of valganciclovir 875 mg once daily with a high-fat meal containing approximately 600 calories resulted in a 30% increase in the steady-state area under the plasma concentration-time curve (AUC) and a 14% increase in the peak plasma concentration (Cmax) of ganciclovir, with no delay in the time to reach peak plasma concentration (Tmax).', 'high-fat foods', 'Moderate', 'The manufacturer recommends that valganciclovir be taken with meals.', NULL, NULL, 'DDInter', 0);
