INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3380, 29091, 'Venetoclax', 'Food enhances the oral bioavailability of venetoclax.  Relative to fasting conditions, venetoclax systemic exposure (AUC) increased by approximately 3.4-fold when administered with a low-fat meal and by 5.1- to 5.3-fold when administered with a high-fat meal.', 'high-fat foods', 'Major', 'Venetoclax should be administered with a meal and water at approximately the same time each day.  Patients should avoid consumption of grapefruit products, Seville oranges, and starfruit during treatment with venetoclax.', NULL, NULL, 'DDInter', 0);
